import { useEffect } from "react";
import {
  Route,
  Routes,
  useLocation,
  useNavigationType,
} from "react-router-dom";
import MainWinodw from "./components/MainWindow";

function App() {
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action, pathname]);

  useEffect(() => {
    let title = "";
    let metaDescription = "";

    switch (pathname) {
      case "/":
        title = "";
        metaDescription = "";
        break;
      case "/frame-371":
        title = "";
        metaDescription = "";
        break;
      case "/projects-page-1":
        title = "";
        metaDescription = "";
        break;
      case "/frame-372":
        title = "";
        metaDescription = "";
        break;
      case "/projects-page-2":
        title = "";
        metaDescription = "";
        break;
      case "/desktop-2":
        title = "";
        metaDescription = "";
        break;
      case "/frame-373":
        title = "";
        metaDescription = "";
        break;
      case "/projects-page":
        title = "";
        metaDescription = "";
        break;
      case "/desktop-1":
        title = "";
        metaDescription = "";
        break;
      case "/frame-374":
        title = "";
        metaDescription = "";
        break;
      case "/projects-page-3":
        title = "";
        metaDescription = "";
        break;
      case "/frame-375":
        title = "";
        metaDescription = "";
        break;
    }

    if (title) {
      document.title = title;
    }

    if (metaDescription) {
      const metaDescriptionTag = document.querySelector(
        'head > meta[name="description"]',
      );
      if (metaDescriptionTag) {
        metaDescriptionTag.content = metaDescription;
      }
    }
  }, [pathname]);

  return (
    <Routes>
      <Route path="/" element={<MainWinodw />} />
    </Routes>
  );
}
export default App;
