import { gsap } from "gsap";
import { useCallback, useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./Desktop.module.css";

const Desktop = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
  };

  const onHomeTextClick = useCallback(() => {
    navigate("/");
  }, [navigate]);

  const onProjectsTextClick = useCallback(() => {
    navigate("/projects-page");
  }, [navigate]);

  const backgroundRef = useRef(null);

  useEffect(() => {
    gsap.to(backgroundRef.current, {
      duration: 1,
      backgroundImage: `url('/rectangle-33@2x.png')`, // New background image URL
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
    });
  }, []);

  return (
    <div ref={backgroundRef} className={styles.desktop2}>
      <header className={styles.header}>
        <div className={styles.logo}>
        <img
              className={styles.logoChild}
              alt=""
              src="/rectangle-343@2x.png"
            />
        </div>
        <div className={styles.frame}>
          <nav className={styles.navbar}>
            <div className={styles.home} onClick={onHomeTextClick}>Home</div>
            <div className={styles.aboutMe}>About Me</div>
            <div className={styles.projects} onClick={onProjectsTextClick}>Projects</div>
            <div className={styles.contact}>Contact</div>
          </nav>
        </div>
      </header>
      <div className={styles.formContainer}>
        <h2 className={styles.formTitle}>Contact Us</h2>
        <form className={styles.form} onSubmit={handleSubmit}>
          <div className={styles.formGroup}>
            <label htmlFor="name" className={styles.formLabel}>Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              className={styles.formInput}
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="email" className={styles.formLabel}>Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              className={styles.formInput}
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="message" className={styles.formLabel}>Message:</label>
            <textarea
              id="message"
              name="message"
              className={styles.formTextarea}
              value={formData.message}
              onChange={handleChange}
              required
            ></textarea>
          </div>
          <button type="submit" className={styles.formButton}>Submit</button>
        </form>
      </div>
    </div>
  );
};

export default Desktop;
