import gsap from "gsap";
import { useEffect, useRef, useState } from "react";
import Header from "../components/Header";
import styles from "./ProjectsPage.module.css";

const ProjectsPage = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [title, setTitle] = useState("Atlas - ecommerce Website");

  // Background sources
  const backgrounds = [
    "/Atlas.png",
    "/3d_cards.mp4",
    "/bg3.png",
    "/bg4.png"
  ];

  const titles = [
    "Atlas - ecommerce Website",
    "Project 2 - Website Redesign",
    "Project 3 - Mobile App",
    "Project 4 - Marketing Campaign"
  ];

  const textRef = useRef(null);
  const backgroundRef = useRef(null);
  const videoRef = useRef(null); // Reference for video element
  const imagesRef = useRef(null);

  const handleClick = (index) => {
    setCurrentIndex(index);
    setTitle(titles[index]);

    // Animate background and title changes
    if (index === 1) {
      // If the background is a video
      gsap.to(videoRef.current, {
        opacity: 1,
        duration: 1,
        onComplete: () => {
          gsap.fromTo(
            textRef.current,
            { opacity: 0, y: -50 },
            { opacity: 1, y: 0, duration: 1 }
          );
        }
      });
    } else {
      // For static images
      gsap.to(backgroundRef.current, {
        duration: 1,
        backgroundImage: `url(${backgrounds[index]})`,
        onComplete: () => {
          gsap.fromTo(
            textRef.current,
            { opacity: 0, y: -50 },
            { opacity: 1, y: 0, duration: 1 }
          );
        }
      });
      gsap.fromTo(
        imagesRef.current.children,
        { opacity: 0, scale: 0.8 },
        { opacity: 1, scale: 1, duration: 1, stagger: 0.3 }
      );
    }
  };

  useEffect(() => {
    // Initial setup or animations if needed
  }, []);

  return (
    <div className={styles.projectsPage} ref={backgroundRef}>
      {currentIndex === 1 && (
        <video
          ref={videoRef}
          className={styles.backgroundVideo}
          autoPlay
          muted
          loop
          src={backgrounds[currentIndex]}
          style={{ opacity: 0 }} // Initially hide the video
        />
      )}
      <div className={styles.monkeyDLufy} ref={textRef}>
        {title}
      </div>
      <div className={styles.rectangleParent} ref={imagesRef}>
        <img
          className={styles.frameChild}
          alt=""
          src="/Atlas_products.png"
          onClick={() => handleClick(0)} // Example index
        />
        <img
          className={styles.frameItem}
          alt=""
          src="/Dragons.png"
          onClick={() => handleClick(1)} // Example index
        />
        <img
          className={styles.frameItem1}
          alt=""
          src="/rectangle-251@2x.png"
          onClick={() => handleClick(2)} // Example index
        />
        <img
          className={styles.frameItem2}
          alt=""
          src="/rectangle-26@2x.png"
          onClick={() => handleClick(3)} // Example index
        />
      </div>
      <div className={styles.loremIpsumDolorContainer}>
        <p className={styles.loremIpsumDolor}>
          This eCommerce website project is a comprehensive online platform <br />
          designed to deliver a seamless and intuitive shopping experience. <br />
          Developed using the latest web technologies, it integrates a robust back-end system <br />
          with an attractive and responsive front-end interface. The website features advanced <br />
          search and filtering options, secure payment gateways, and a user-friendly checkout process.<br />
          Tailored to meet the needs of modern consumers, this project emphasizes speed, <br />
          security, and accessibility, ensuring that users can easily find and purchase products with confidence. <br />
          With a focus on scalability, the platform is designed to grow alongside the business,<br />
          accommodating an expanding inventory and a growing customer base.
        </p>
      </div>
      <div className="navbar">
        <Header />
      </div>
    </div>
  );
};

export default ProjectsPage;
